    private void crearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_crearActionPerformed
        int size = Integer.parseInt(JOptionPane.showInputDialog("Diga El Tama√±o del Arreglo"));
        edades = new int[size];
        
        for (int i = 0; i < edades.length; i++) {
            edades[i] = (int)(Math.random()*100+1);
        }
        
        JOptionPane.showMessageDialog(rootPane, "Completo");
    }//GEN-LAST:event_crearActionPerformed

    private void imprimirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_imprimirActionPerformed
        
        for (int i = 0; i < edades.length; i++) {
            JOptionPane.showConfirmDialog(rootPane, edades[i]);
        }
        
    }//GEN-LAST:event_imprimirActionPerformed

    private void buscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buscarActionPerformed
        int num = Integer.parseInt(JOptionPane.showInputDialog("Ingrese La Edad a Buscar"));
        boolean band = false;
        int repeat = 0;
        
        for (int i = 0; i < edades.length; i++) {
            if (num == edades[i]) {
                band = true;
                JOptionPane.showMessageDialog(rootPane, "Se Encuentra en la Posicion "+i);
                repeat++;
            }
        }
        
        JOptionPane.showMessageDialog(rootPane, "Se repite "+repeat+" veces");
        
        if (!band) {
            JOptionPane.showMessageDialog(rootPane, "No se ha Encontrado");
        }
    }//GEN-LAST:event_buscarActionPerformed

    private void modificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modificarActionPerformed
        for (int i = 0; i < edades.length; i++) {
            if (edades[i]%2 != 0) {
                edades[i]++;
            }
        }
        
        JOptionPane.showMessageDialog(rootPane, "Se ha Realizado La Modificacion");
    }//GEN-LAST:event_modificarActionPerformed

    private void ordenarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ordenarActionPerformed
        Arrays.sort(edades);
        
        for (int i = 1; i <= edades.length; i++) {
            System.out.print(edades[i-1]+",");
            if (i%10==0) {
                System.out.println("");
            }
        }
    }//GEN-LAST:event_ordenarActionPerformed

